-- Example 2: Shopping Website - Expanded dump with 500 rows and precomputed CRCs
DROP DATABASE IF EXISTS example2_shopping;
CREATE DATABASE example2_shopping CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE example2_shopping;

-- Lookup tables
CREATE TABLE PRODUCT_CATEGORY ( category_id INT AUTO_INCREMENT PRIMARY KEY, category_name VARCHAR(100) NOT NULL );
CREATE TABLE BRAND ( brand_id INT AUTO_INCREMENT PRIMARY KEY, brand_name VARCHAR(100) NOT NULL );
CREATE TABLE COLOUR ( colour_id INT AUTO_INCREMENT PRIMARY KEY, colour_name VARCHAR(50) NOT NULL );
CREATE TABLE SIZE ( size_id INT AUTO_INCREMENT PRIMARY KEY, size_name VARCHAR(50) NOT NULL );
CREATE TABLE SUPPLIER ( supplier_id INT AUTO_INCREMENT PRIMARY KEY, supplier_name VARCHAR(150) NOT NULL );

-- Core entities
CREATE TABLE PRODUCT ( product_id INT AUTO_INCREMENT PRIMARY KEY, product_name VARCHAR(200) NOT NULL, category_id INT NOT NULL, brand_id INT NOT NULL, colour_id INT NOT NULL, size_id INT NOT NULL, supplier_id INT NOT NULL, price DECIMAL(10,2) NOT NULL, CONSTRAINT fk_product_category FOREIGN KEY (category_id) REFERENCES PRODUCT_CATEGORY(category_id), CONSTRAINT fk_product_brand FOREIGN KEY (brand_id) REFERENCES BRAND(brand_id), CONSTRAINT fk_product_colour FOREIGN KEY (colour_id) REFERENCES COLOUR(colour_id), CONSTRAINT fk_product_size FOREIGN KEY (size_id) REFERENCES SIZE(size_id), CONSTRAINT fk_product_supplier FOREIGN KEY (supplier_id) REFERENCES SUPPLIER(supplier_id) );
CREATE TABLE `USER` ( username VARCHAR(50) PRIMARY KEY, first_name VARCHAR(100) NOT NULL, last_name VARCHAR(100) NOT NULL, email VARCHAR(150) NOT NULL );
CREATE TABLE ADDRESS ( address_id INT AUTO_INCREMENT PRIMARY KEY, house_unit_no VARCHAR(20), address_line1 VARCHAR(200), address_line2 VARCHAR(200), city VARCHAR(100), region VARCHAR(100), postal_code VARCHAR(20), country VARCHAR(100) );
CREATE TABLE USER_ADDRESS ( address_id INT NOT NULL, username VARCHAR(50) NOT NULL, PRIMARY KEY (address_id, username), CONSTRAINT fk_ua_address FOREIGN KEY (address_id) REFERENCES ADDRESS(address_id), CONSTRAINT fk_ua_user FOREIGN KEY (username) REFERENCES `USER`(username) );

CREATE TABLE CUSTOMER_ORDER ( order_id INT AUTO_INCREMENT PRIMARY KEY, username VARCHAR(50) NOT NULL, shipping_address_id INT NOT NULL, billing_address_id INT NOT NULL, order_discount DECIMAL(5,2) DEFAULT 0, order_date DATETIME DEFAULT CURRENT_TIMESTAMP, CONSTRAINT fk_co_user FOREIGN KEY (username) REFERENCES `USER`(username), CONSTRAINT fk_co_ship_addr FOREIGN KEY (shipping_address_id) REFERENCES ADDRESS(address_id), CONSTRAINT fk_co_bill_addr FOREIGN KEY (billing_address_id) REFERENCES ADDRESS(address_id) );
CREATE TABLE CUSTOMER_ORDER_LINE ( order_line_id INT AUTO_INCREMENT PRIMARY KEY, order_id INT NOT NULL, product_id INT NOT NULL, qty INT NOT NULL, discount DECIMAL(5,2) DEFAULT 0, price DECIMAL(10,2) NOT NULL, CONSTRAINT fk_col_order FOREIGN KEY (order_id) REFERENCES CUSTOMER_ORDER(order_id), CONSTRAINT fk_col_product FOREIGN KEY (product_id) REFERENCES PRODUCT(product_id) );
CREATE TABLE SUPPLIER_ORDER ( order_id INT AUTO_INCREMENT PRIMARY KEY, supplier_id INT NOT NULL, order_discount DECIMAL(5,2) DEFAULT 0, order_date DATETIME DEFAULT CURRENT_TIMESTAMP, CONSTRAINT fk_so_supplier FOREIGN KEY (supplier_id) REFERENCES SUPPLIER(supplier_id) );
CREATE TABLE SUPPLIER_ORDER_LINE ( order_line_id INT AUTO_INCREMENT PRIMARY KEY, order_id INT NOT NULL, product_id INT NOT NULL, qty INT NOT NULL, discount DECIMAL(5,2) DEFAULT 0, price DECIMAL(10,2) NOT NULL, CONSTRAINT fk_sol_order FOREIGN KEY (order_id) REFERENCES SUPPLIER_ORDER(order_id), CONSTRAINT fk_sol_product FOREIGN KEY (product_id) REFERENCES PRODUCT(product_id) );

